DROP TABLE IF EXISTS "Track" CASCADE;

CREATE TABLE "Track"
(
    "TrackId" INT NOT NULL,
    "Name" VARCHAR(200) NOT NULL,
    "AlbumId" INT,
    "MediaTypeId" INT NOT NULL,
    "GenreId" INT,
    "Composer" VARCHAR(220),
    "Milliseconds" INT NOT NULL,
    "Bytes" INT,
    "UnitPrice" NUMERIC(10,2) NOT NULL,
    CONSTRAINT "PK_Track" PRIMARY KEY  ("TrackId")
);



-- GROUPBY LETS GO !!!
-- pomaha nam vytvarat zoskupenia a nasledne na ne vieme aplikovat agregacnu funkciu (count, sum ...)
-- poradie vykonavanie v postgresql
-- FROM -> WHERE -> GROUP BY -> HAVING -> SELECT -> DISTINCT -> ORDER BY -> LIMIT

SELECT *
FROM "Track"

-- tieto dva prikazy su uplne to iste
SELECT DISTINCT "GenreId"
FROM "Track"

SELECT "GenreId"
FROM "Track"
GROUP BY "GenreId"
--

-- žáner | počet pesničiek v žánri
SELECT  "GenreId", COUNT("GenreId")
FROM "Track"
GROUP BY "GenreId"

SELECT "GenreId" ,COUNT("GenreId")
FROM "Track"
WHERE "Bytes" > 8032162
GROUP BY "GenreId"


-- Samotne group by bez agregacnej funkcie funguje ako DISTINCT a vrati unikatne hodnoty lebo na pozadi sa ako keby vytvoria tieto skupinky  


-- Co ked chceme pre kazdy zaner pocet skladieb v DB 

SELECT *
FROM "Track"

-- Kolko stoja dva najdrahsie albumy  ak v nich chcem kupit vsetky skladby 
SELECT "AlbumId", SUM("UnitPrice") AS total_price
FROM "Track"
GROUP BY "AlbumId"
ORDER BY total_price DESC
LIMIT 2
-- Spolu s GROUP BY sa pouziva prikaz HAVING ktory sluzi na filtrovanie skupin podla podmienky
-- Cize where filtuje riadky ktore pojdu do group by a nasledne having filtruje vysledne grupy
SELECT *
FROM "Track"

-- Vyfiltruj idcka albumov, ktorych suma cien skladieb je vacsia ako 8
SELECT "AlbumId", SUM("UnitPrice")
FROM "Track"
GROUP BY "AlbumId"
HAVING SUM("UnitPrice") > 8


-- GROUP BY vieme aplikovat aj na viac stlpcov naraz

-- Toto sú všetky kombinácie GenreId a AlbumId
SELECT "GenreId","AlbumId"
FROM "Track"
GROUP BY "GenreId", "AlbumId"

-- Toto sú počty všetkých kombinácií 
SELECT "GenreId","AlbumId", COUNT(*)
FROM "Track"
GROUP BY "GenreId", "AlbumId"

-- Kolko stoja jednotlive kombinacie zanrov a albumov
SELECT "GenreId","AlbumId", SUM("UnitPrice")
FROM "Track"
GROUP BY "GenreId", "AlbumId"






